import Foundation
import UIKit

struct PresentableCategory {
    var isGroup = false
    var isChosen = false
    var isExpanded = false
    var showString : String!
    let categories : [String]!

    init(_ show: String, categoryList: [String]){
        self.isGroup = true
        self.showString = show
        self.categories = categoryList
    }

    init(_ show: String, _ single: String){
        self.isGroup = false
        self.showString = show
        self.categories = [single]
    }

}


